// ----------------------------------------------------------------------
// Property implementation template
// ----------------------------------------------------------------------

template <typename T, vk::StructureType SType> class PropertyImpl : public Property {
  public:
    std::string get_name() const override {
        return vk::to_string(SType);
    }
    vk::StructureType get_structure_type() const override {
        return SType;
    }
    void* get_structure_ptr() override {
        return &data;
    }
    void set_pnext(void* p_next) override {
        data.pNext = p_next;
    }
    T& get() {
        return data;
    }
    const T& get() const {
        return data;
    }

  private:
    T data{};
};

